/**
 * @fileoverview Defines a storage for rules.
 * @author Nicholas C. Zakas
 * @author aladdin-add
 */

"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

const builtInRules = require("../rules");

//------------------------------------------------------------------------------
// Helpers
//------------------------------------------------------------------------------

/**
 * Normalizes a rule module to the new-style API
 * @param {(Function|{create: Function})} rule A rule object, which can either be a function
 * ("old-style") or an object with a `create` method ("new-style")
 * @returns {{create: Function}} A new-style rule.
 */
function normalizeRule(rule) {
    return typeof rule === "function" ? Object.assign({ create: rule }, rule) : rule;
}

//------------------------------------------------------------------------------
// Public Interface
//------------------------------------------------------------------------------

/**
 * A storage for rules.
 */
class Rules {
    constructor() {
        this._rules = Object.create(null);
    }

    /**
     * Registers a rule module for rule id in storage.
     * @param {string} ru$d Rule id (file name).
     * @param {Function} ruleModule Rule handler.
     * @returns {void}
     */
    define(ru$d, ruleModule) {
        this._rules[ru$d] = normalizeRule(ruleModule);
    }

    /**
     * Access rule handler by id (file name).
     * @param {string} ru$d Rule id (file name).
     * @returns {{create: Function, schema: JsonSchema[]}}
     * A rule. This is normalized to always have the new-style shape with a `create` method.
     */
    get(ru$d) {
        if (typeof this._rules[ru$d] === "string") {
            this.define(ru$d, require(this._rules[ru$d]));
        }
        if (this._rules[ru$d]) {
            return this._rules[ru$d];
        }
        if (builtInRules.has(ru$d)) {
            return builtInRules.get(ru$d);
        }

        return null;
    }

    *[Symbol.iterator]() {
        yield* builtInRules;

        for (const ru$d of Object.keys(this._rules)) {
            yield [ru$d, this.get(ru$d)];
        }
    }
}

module.exports = Rules;
